import { useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Grid,
} from "@mui/material";

const ContactUsDialog = ({ open, onClose }) => {
  const [formData, setFormData] = useState({
    FirstName: "",
    LastName: "",
    jopTitle: "",
    companyName: "",
    country: "",
    BusinessEmail: "",
    mobile: "",
    solution: "",
  });

  const showAlert = () => {
    Swal.fire({
      text: "Thank You For Contacting Us",
      icon: "success",
    });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/database", formData);
      setFormData({
        FirstName: "",
        LastName: "",
        jopTitle: "",
        companyName: "",
        country: "",
        BusinessEmail: "",
        mobile: "",
        solution: "",
      });
      showAlert();
      onClose(); // Close dialog after submit
    } catch (error) {
      console.error("Error adding profile:", error);
    }
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="sm">
      <DialogTitle>Contact Us Form</DialogTitle>
      <DialogContent>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            {[
              { label: "First Name", name: "FirstName", type: "text" },
              { label: "Last Name", name: "LastName", type: "text" },
              { label: "Job Title", name: "jopTitle", type: "text" },
              { label: "Company Name", name: "companyName", type: "text" },
              { label: "Country", name: "country", type: "text" },
              { label: "Mobile", name: "mobile", type: "number" },
              { label: "Business Email", name: "BusinessEmail", type: "email" },
            ].map(({ label, name, type }) => (
              <Grid item xs={12} sm={6} key={name}>
                <TextField
                  fullWidth
                  label={label}
                  name={name}
                  type={type}
                  value={formData[name]}
                  onChange={handleChange}
                  variant="outlined"
                />
              </Grid>
            ))}

            <Grid item xs={12}>
              <FormControl fullWidth>
                <InputLabel>Solution</InputLabel>
                <Select name="solution" value={formData.solution} onChange={handleChange}>
                  <MenuItem value="">Select Solution</MenuItem>
                  <MenuItem value="Research & Analytics">Research & Analytics</MenuItem>
                  <MenuItem value="Panel Services">Panel Services</MenuItem>
                  <MenuItem value="Community Management">Community Management</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </form>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">
          Cancel
        </Button>
        <Button type="submit" variant="contained" color="primary" onClick={handleSubmit}>
          Send The Invite
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default ContactUsDialog;
