import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./ResetPassowrd.css";
import Logo from "../../../Media/Logo.jpg";
import { FaListUl } from "react-icons/fa";
import axios from "axios";
import Swal from "sweetalert2";

function ResetPassword() {
    const [newPassword, setNewPassword] = useState("");
    const [authpassword, setauthPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [togglelist, settogglelist] = useState(false);
    const [passContent, setpassContent] = useState("");
    const [isAuthorized, setIsAuthorized] = useState(false);

    useEffect(() => {
        settogglelist(false);
        const fetchPassContent = async () => {
            try {
                const response = await axios.get("https://api.data-nile.com/password");
                setpassContent(response.data.password);
            } catch (error) {
                console.error("Error fetching password:", error);
            }
        };
        fetchPassContent();
    }, []);

    const showAlert = (text, icon) => {
        Swal.fire({ text, icon });
    };

    const handlePasswordSubmit = () => {
        if (authpassword === passContent) {
            setIsAuthorized(true);
        } else {
            showAlert("Incorrect password! Please try again.", "error");
        }
    };

    const handleResetPassword = (event) => {
        event.preventDefault();

        if (!newPassword.trim() || !confirmPassword.trim()) {
            showAlert("Password fields cannot be empty", "error");
            return;
        }

        if (newPassword !== confirmPassword) {
            showAlert("Passwords do not match! Please try again.", "error");
            return;
        }

        axios
            .post("https://api.data-nile.com/password", { password: newPassword })
            .then(() => showAlert("Password reset successfully!", "success"))
            .catch((error) => console.error("Error updating password:", error));
    };

    if (!isAuthorized) {
        return (
            <div className="password-form">
                <h2>Password Required</h2>
                <input
                    type="password"
                    placeholder="Enter password"
                    value={authpassword}
                    onChange={(e) => setauthPassword(e.target.value)}
                />
                <button className="entr" onClick={handlePasswordSubmit}>
                    Submit
                </button>
            </div>
        );
    }

    return (
        <>
            <div className="TOG" onClick={() => settogglelist((prev) => !prev)}>
                <FaListUl />
            </div>
            <div className="Logo">
                <img src={Logo} alt="Logo" />
            </div>
            <div className={togglelist ? "AsideSection" : "AsideSection displaynoneMoble"}>
                <ul>
                    <Link to="/Dachboeard" onClick={() => settogglelist(false)}>
                        <li><span>Edit Page</span></li>
                    </Link>
                    <Link to="/Invits" onClick={() => settogglelist(false)}>
                        <li><span>Invits</span></li>
                    </Link>
                    <Link to="/ResetPassword" onClick={() => settogglelist(false)}>
                        <li><span>Reset Password</span></li>
                    </Link>
                    <li onClick={() => (window.location.href = "/")}><span>Logout</span></li>
                </ul>
            </div>
            <div className="Container">
                <div className="password-reset-form">
                    <h2>Reset Password</h2>
                    <input
                        type="password"
                        placeholder="Enter new password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Confirm new password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                    />
                    <button className="resetpassmob" onClick={handleResetPassword}>
                        Reset Password
                    </button>
                </div>
            </div>
        </>
    );
}

export default ResetPassword;
