import axios from "axios";
import Swal from "sweetalert2";
import "./ContactUs.css";
import { useState } from "react";

function ContactUs() {
  const [formData, setFormData] = useState({
    FirstName: "",
    LastName: "",
    jopTitle: "",
    companyName: "",
    country: "",
    BusinessEmail: "",
    mobile: "",
    solution: "",
  });
  const [ToggleForm, setToggleForm] = useState(false);

  const showAlert = () => {
    Swal.fire({
      text: "Thank You For Contacting Us",
      icon: "success",
    });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/database", formData);
      console.log(response.data);
      setFormData({
        FirstName: "",
        LastName: "",
        jopTitle: "",
        companyName: "",
        country: "",
        BusinessEmail: "",
        mobile: "",
        solution: "",
      });
      showAlert();
      setToggleForm(false);
    } catch (error) {
      console.error("Error adding profile:", error);
    }
  };

  return (
    <>
      <div className="ContactUs">
        <h2>Contact us</h2>
        <h4>Please click below to contact us</h4>
        <button style={{ background: "#333", width: "20%" }} onClick={() => setToggleForm(!ToggleForm)}>
          Contact Us
        </button>
      </div>

      {ToggleForm && (
        <div className="ContactUsForm displayblock">
          <form onSubmit={handleSubmit}>
            <div className="CloseForm" onClick={() => setToggleForm(false)}>
              <div></div>
              <div></div>
            </div>
            <h1>Contact Us Form</h1>

            {[
              { label: "First Name", name: "FirstName", type: "text" },
              { label: "Last Name", name: "LastName", type: "text" },
              { label: "Job Title", name: "jopTilte", type: "text" },
              { label: "Company Name", name: "companyName", type: "text" },
              { label: "Country", name: "country", type: "text" },
              { label: "Mobile", name: "mobile", type: "number" },
              { label: "Business Email", name: "BusinessEmail", type: "email" },
            ].map(({ label, name, type }) => (
              <label key={name}>
                <span>{label}:</span>
                <input type={type} name={name} value={formData[name]} onChange={handleChange} />
              </label>
            ))}

            <label>
              <span>Solution:</span>
              <select name="solution" value={formData.solution} onChange={handleChange}>
                <option value="">Select Solution</option>
                <option value="Research & Analytics">Research & Analytics</option>
                <option value="Panel Services">Panel Services</option>
                <option value="Community Management">Community Management</option>
              </select>
            </label>

            <div className="BTN">
              <button type="submit">Send The Invite</button>
            </div>
          </form>
        </div>
      )}
    </>
  );
}

export default ContactUs;
